import React, { useState } from "react";
import AddIcon from '@mui/icons-material/Add';

const CreteNote =() =>{

    const [note,setNote] = useState({
        title : "",
        content : ""
    });

    const InputValue =(event) =>{
        const {name, value} = event.target
        setNote((preData) => {
            return {
                ...preData,
            }
        })
    }

   return(
     <>
        <form>
            <input type="text" value={note.title} onChange={InputValue} placeholder ="Enter your Title" name="Title"  />
            <textarea  rows="5" value={note.content} onChange={InputValue}  column="6" placeholder="Enter your Remainder" />
            <button>
                <AddIcon className="plus" />
            </button>

        </form>
     </>
   );
};

export default CreteNote;
