import React, { useState } from "react";

const Div = () =>{

  
   const state =useState();

   const [names,cdatas] =useState();
   const [fullName,ddatas] = useState();
   const [fileImage,vdata] = useState();
   const [lastName,laste] = useState();

    const inputValue =(event) =>{
     // console.log(event.target.value);
      cdatas(event.target.value);
    }
    const handleImage =(event) =>{
      vdata(URL.craeteObjectURL(event.target.files[0]));
      console.log(event.target.files[0]);
    }

    const fullNaam =() =>{
      ddatas(names);
    }


 return (
   <>  
   <form onSubmit={fullNaam}> 
   <h1>Hello {fullName} {lastName} </h1>
   <input type= "text" placeholder="Enter The Name" onChange={inputValue}  value={names} />
   <br />
 
   <input type="file"  onChange={handleImage} />
   <br />
   <img src = {fileImage} />
   <br />
   <button type="submit" >Submit</button>
   </form>
   </>
 );
};

export default Div;
